import React, { useState } from 'react';
import MyAccount from './MyAccount'; // Import MyAccount component
import MyListings from './MyListings'; // Import MyListings component

const Profile = () => {
  const [selectedOption, setSelectedOption] = useState('My Account');

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="profile-container">
      <div className="sidebar">
        <div className="sidebar-option" onClick={() => handleOptionClick('My Account')}>
          My Account
        </div>
        <div className="sidebar-option" onClick={() => handleOptionClick('My Listings')}>
          My Listings
        </div>
      </div>
      <div className="main-content">
        {selectedOption === 'My Account' ? <MyAccount /> : <MyListings />}
      </div>
    </div>
  );
};

export default Profile;
