import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './MessagePanel.css';

const conversations = [
  { id: 1, name1: 'John Doe', name2: 'Matt'},
  { id: 2, name1: 'Jane Smith', name2: 'Matt' },
  { id: 3, name1: 'Matt', name2: 'Alice Johnson' },
];

const signedInUserName = 'Matt'

const messages = [
  { id: 1, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 0},
  { id: 2, conversation_id: 1, sender: 'Matt', content: 'Okay! when are you free to meet?', timeSent: 1},
  { id: 3, conversation_id: 1, sender: 'John Doe', content: 'Would tomorrow at 5pm work for you?', timeSent: 2},
  { id: 4, conversation_id: 1, sender: 'Matt', content: 'Yes that\'s perfect. see you then.', timeSent: 3},
  { id: 5, conversation_id: 2 , sender: 'Matt', content: 'Hey I\'d like to buy your item!', timeSent: 4},
  { id: 6, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 5},
  { id: 7, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 6},
  { id: 8, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 7},
  { id: 9, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 8},
  { id: 10, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 9},
  { id: 11, conversation_id: 1, sender: 'John Doe', content: 'Hey I\'d like to buy your item!', timeSent: 10}
]

const MessagePanel = () => {
  const [selectedConversation, setSelectedConversation] = useState(conversations[0]);

  const handleConversationSelect = (conversation) => {
    setSelectedConversation(conversation);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3 sidebar">
          <h2>Conversations</h2>
          <ul className="list-group">
            {conversations.map(conversation => (
              <li
                key={conversation.id}
                className={`list-group-item ${selectedConversation.id === conversation.id ? 'active' : ''}`}
                onClick={() => handleConversationSelect(conversation)}
              >
                {conversation.name}
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-9">
          <div className="messaging-panel">
            <h2>Messages with {selectedConversation.name}</h2>
            <div className="message-container">
              {/* Display messages here */}
            </div>
            {/* Add message input and send button */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessagePanel;
